import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  Box,
  Button,
  TextField,
  Typography,
  Container,
  Alert,
  InputAdornment,
  Link,
} from '@mui/material';
import PersonOutlineOutlinedIcon from '@mui/icons-material/PersonOutlineOutlined';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import CalendarTodayOutlinedIcon from '@mui/icons-material/CalendarTodayOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';

import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const validationSchema = Yup.object({
  name: Yup.string()
    .required('Name is required')
    .min(2, 'Name should be of minimum 2 characters length'),
  email: Yup.string()
    .email('Enter a valid email')
    .required('Email is required'),
  dob: Yup.date()
    .required('Date of birth is required')
    .max(new Date(), 'Date of birth cannot be in the future'),
  password: Yup.string()
    .min(6, 'Password should be of minimum 6 characters length')
    .required('Password is required'),
});

// Define colors based on the image
const primaryDark = '#2c3e50'; // Dark background of the form container
const accentColor = '#00bcd4'; // Teal/Cyan accent color
const lightText = '#ecf0f1'; // Very light grey/white for main text/icons
const mediumText = '#bdc3c7'; // Medium grey for labels and secondary text
const formBackground = '#3b5066'; // Background color for input fields
const pageBackgroundDark = '#34495e'; // Dark part of the page background

// Reusable styles for TextField components
const inputSx = {
  borderRadius: '25px',
  borderTopRightRadius: '4px',
  borderBottomRightRadius: '4px',
  borderTopLeftRadius: '4px',
  borderBottomLeftRadius: '4px',
  backgroundColor: formBackground,
  color: lightText, // Input text color
  '& fieldset': { border: 'none' },
  paddingLeft: '14px', // Adjust padding for icon
};

const inputLabelSx = {
  color: mediumText, // Label color
  fontSize: '1rem',
  fontWeight: 'bold',
  position: 'relative',
  transform: 'none',
  marginBottom: '4px',
  top: 'auto',
  left: 'auto',
};

export const RegisterForm = () => {
  const { register, error } = useAuth();
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      dob: '',
      password: '',
    },
    validationSchema,
    onSubmit: async (values) => {
      await register(values);
      navigate('/login');
    },
  });

  return (
     <Box
      sx={{
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        // Updated background to better match the split design
        background: `linear-gradient(to bottom, ${accentColor} 0%, ${accentColor} 50%, ${pageBackgroundDark} 50%, ${pageBackgroundDark} 100%)`,
         // Consider adjusting background size/position if needed for different screen sizes
      }}
    >
      <Container component="main" maxWidth="xs">
        <Box
          sx={{
            paddingTop: '60px', // Add padding to accommodate the absolute positioned header
            paddingX: 4,
            paddingBottom: 4,
            borderRadius: 2,
            backgroundColor: primaryDark,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
             boxShadow: '0px 0px 20px rgba(0,0,0,0.5)',
             overflow: 'hidden', // Hide overflowing elements like the header button corners
             position: 'relative' // Needed for absolute positioning of header button
          }}
        >
           {/* "SIGN UP" Header Button - Adjusted positioning and shape */}
          <Button
            variant="contained"
            fullWidth
            sx={{
               position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: 50, // Adjust height to match visual
              // Match the sharp bottom corners and rounded top corners of the header in the image
              borderBottomLeftRadius: 0,
              borderBottomRightRadius: 0,
              borderTopLeftRadius: 8,
              borderTopRightRadius: 8,
              backgroundColor: accentColor,
              '&:hover': {
                backgroundColor: accentColor, // Keep the same color on hover
                opacity: 0.9 // Slight opacity change
              },
              color: 'white',
              fontWeight: 'bold',
              fontSize: '1.2rem',
               padding: '12px 16px', // Adjust padding to match visual weight
               zIndex: 1 // Ensure button is above other content
            }}
             onClick={() => navigate('/register')}
          >
            SIGN UP
          </Button>

           {/* Content below the header */}
           <Box sx={{ mt: 8, width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>

          {/* User Icon (Placeholder for registration) */}
           <Box
            sx={{
              width: 80,
              height: 80,
              borderRadius: '50%',
              backgroundColor: '#556c7b', // A lighter shade for the icon background
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              mb: 3,
            }}
          >
            <PersonOutlineOutlinedIcon sx={{ fontSize: 50, color: lightText }} /> {/* Light icon color */}
          </Box>

          {error && (
            <Alert severity="error" sx={{ mt: 2, width: '100%', mb: 2 }}>
              {error}
            </Alert>
          )}

          <Box component="form" onSubmit={formik.handleSubmit} sx={{ width: '100%' }}>
            {/* Name Field */}
            <TextField
              margin="normal"
              fullWidth
              id="name"
              name="name"
              label="Full Name"
              autoComplete="name"
              autoFocus
              value={formik.values.name}
              onChange={formik.handleChange}
              error={formik.touched.name && Boolean(formik.errors.name)}
              helperText={formik.touched.name && formik.errors.name}
               InputProps={{
                startAdornment: (
                  <InputAdornment position="start" sx={{ color: mediumText }}>
                    <PersonOutlineOutlinedIcon />
                  </InputAdornment>
                ),
                sx: inputSx
              }}
               InputLabelProps={{ sx: inputLabelSx, shrink: true }}
            />
            {/* Email Field */}
            <TextField
              margin="normal"
              fullWidth
              id="email"
              name="email"
              label="Email Address"
              autoComplete="email"
              value={formik.values.email}
              onChange={formik.handleChange}
              error={formik.touched.email && Boolean(formik.errors.email)}
              helperText={formik.touched.email && formik.errors.email}
               InputProps={{
                startAdornment: (
                  <InputAdornment position="start" sx={{ color: mediumText }}>
                    <EmailOutlinedIcon />
                  </InputAdornment>
                ),
                sx: inputSx
              }}
               InputLabelProps={{ sx: inputLabelSx, shrink: true }}
            />
            {/* Date of Birth Field */}
            <TextField
              margin="normal"
              fullWidth
              id="dob"
              name="dob"
              label="Date of Birth"
              type="date"
              InputLabelProps={{
                shrink: true,
                sx: inputLabelSx
              }}
               InputProps={{
                 startAdornment: (
                  <InputAdornment position="start" sx={{ color: mediumText }}>
                    <CalendarTodayOutlinedIcon />
                  </InputAdornment>
                ),
                sx: inputSx
              }}
              value={formik.values.dob}
              onChange={formik.handleChange}
              error={formik.touched.dob && Boolean(formik.errors.dob)}
              helperText={formik.touched.dob && formik.errors.dob}
            />
            {/* Password Field */}
            <TextField
              margin="normal"
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="new-password"
              value={formik.values.password}
              onChange={formik.handleChange}
              error={formik.touched.password && Boolean(formik.errors.password)}
              helperText={formik.touched.password && formik.errors.password}
               InputProps={{
                startAdornment: (
                  <InputAdornment position="start" sx={{ color: mediumText }}>
                    <LockOutlinedIcon />
                  </InputAdornment>
                ),
                sx: inputSx
              }}
               InputLabelProps={{ sx: inputLabelSx, shrink: true }}
            />

            {/* Sign Up Button */}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                mt: 3,
                mb: 2,
                backgroundColor: accentColor, // Use accent color for button
                 '&:hover': {
                  backgroundColor: accentColor, // Keep the same color on hover
                   opacity: 0.9 // Slight opacity change
                },
                color: 'white',
                fontWeight: 'bold',
                fontSize: '1.2rem',
                borderRadius: '25px', // Rounded corners for the button
              }}
              disabled={formik.isSubmitting}
            >
              SIGN UP
            </Button>

            {/* Link to Sign In */}
            <Button
              fullWidth
              variant="text"
              sx={{ color: accentColor, fontWeight: 'bold', fontSize: '0.9rem' }}
              onClick={() => navigate('/login')}
            >
              ALREADY HAVE AN ACCOUNT? SIGN IN
            </Button>
          </Box>
          </Box>
        </Box>
      </Container>
     </Box>
  );
};
