import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  Box,
  Button,
  TextField,
  Typography,
  Container,
  Alert,
  InputAdornment,
  Link,
} from '@mui/material';
import PersonOutlineOutlinedIcon from '@mui/icons-material/PersonOutlineOutlined';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import CalendarTodayOutlinedIcon from '@mui/icons-material/CalendarTodayOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';

import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const validationSchema = Yup.object({
  name: Yup.string()
    .required('Name is required')
    .min(2, 'Name should be of minimum 2 characters length'),
  email: Yup.string()
    .email('Enter a valid email')
    .required('Email is required'),
  dob: Yup.date()
    .required('Date of birth is required')
    .max(new Date(), 'Date of birth cannot be in the future'),
  password: Yup.string()
    .min(6, 'Password should be of minimum 6 characters length')
    .required('Password is required'),
});

export const RegisterForm = () => {
  const { register, error } = useAuth();
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      dob: '',
      password: '',
    },
    validationSchema,
    onSubmit: async (values) => {
      await register(values);
      navigate('/dashboard');
    },
  });

   // Define colors based on the image
  const primaryDark = '#2c3e50'; // Dark background
  const accentColor = '#00bcd4'; // Teal/Cyan accent
  const lightText = '#ecf0f1'; // Very light grey/white for main text
  const mediumText = '#bdc3c7'; // Medium grey for labels and icons

  return (
     <Box
      sx={{
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#34495e', // Slightly lighter background for the whole page
         // Add gradient background to match image
        backgroundImage: 'linear-gradient(to bottom right, #00bcd4, #34495e)',
      }}
    >
      <Container component="main" maxWidth="xs">
        <Box
          sx={{
            padding: 4,
            borderRadius: 2,
            backgroundColor: primaryDark, // Use dark background color
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
             boxShadow: '0px 0px 20px rgba(0,0,0,0.5)',
             overflow: 'hidden' // Hide overflowing elements like the header button corners
          }}
        >
           {/* "SIGN UP" Header/Button */}
          <Button
            variant="contained"
            fullWidth
            sx={{
               // Match the sharp top corners of the header in the image
              borderBottomLeftRadius: 0,
              borderBottomRightRadius: 0,
              borderTopLeftRadius: 8,
              borderTopRightRadius: 8,
              marginTop: -4, // Pull it up to the top edge of the container
              mb: 4,
              backgroundColor: accentColor,
              '&:hover': {
                backgroundColor: accentColor, // Keep the same color on hover
                opacity: 0.9 // Slight opacity change
              },
              color: 'white',
              fontWeight: 'bold',
              fontSize: '1.2rem',
               padding: '12px 16px' // Adjust padding to match visual weight
            }}
             onClick={() => navigate('/register')}
          >
            SIGN UP
          </Button>

          {/* User Icon (Placeholder for registration) */}
           <Box
            sx={{
              width: 80,
              height: 80,
              borderRadius: '50%',
              backgroundColor: '#556c7b', // A lighter shade for the icon background
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              mb: 3,
            }}
          >
            <PersonOutlineOutlinedIcon sx={{ fontSize: 50, color: lightText }} /> {/* Light icon color */}
          </Box>

          {error && (
            <Alert severity="error" sx={{ mt: 2, width: '100%', mb: 2 }}>
              {error}
            </Alert>
          )}

          <Box component="form" onSubmit={formik.handleSubmit} sx={{ width: '100%' }}>
            {/* Name Field */}
            <TextField
              margin="normal"
              fullWidth
              id="name"
              name="name"
              label="Full Name"
              autoComplete="name"
              autoFocus
              value={formik.values.name}
              onChange={formik.handleChange}
              error={formik.touched.name && Boolean(formik.errors.name)}
              helperText={formik.touched.name && formik.errors.name}
               InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <PersonOutlineOutlinedIcon sx={{ color: mediumText }} />
                  </InputAdornment>
                ),
                 sx: { 
                  // Rounded left corners only - challenging with default MUI TextField
                  // Approximating with full border radius for now
                  borderRadius: '25px', 
                  backgroundColor: '#3b5066', 
                  color: lightText,
                   '& fieldset': { border: 'none' }, // Remove default border
                },
              }}
               InputLabelProps={{
                sx: { color: mediumText }, // Label color
              }}
            />
            {/* Email Field */}
            <TextField
              margin="normal"
              fullWidth
              id="email"
              name="email"
              label="Email Address"
              autoComplete="email"
              value={formik.values.email}
              onChange={formik.handleChange}
              error={formik.touched.email && Boolean(formik.errors.email)}
              helperText={formik.touched.email && formik.errors.email}
               InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <EmailOutlinedIcon sx={{ color: mediumText }} />
                  </InputAdornment>
                ),
                 sx: { 
                  // Rounded left corners only - challenging with default MUI TextField
                  // Approximating with full border radius for now
                  borderRadius: '25px', 
                  backgroundColor: '#3b5066', 
                  color: lightText,
                   '& fieldset': { border: 'none' }, // Remove default border
                },
              }}
               InputLabelProps={{
                sx: { color: mediumText }, // Label color
              }}
            />
            {/* Date of Birth Field */}
            <TextField
              margin="normal"
              fullWidth
              id="dob"
              name="dob"
              label="Date of Birth"
              type="date"
              InputLabelProps={{
                shrink: true,
                 sx: { color: mediumText }, // Label color
              }}
               InputProps={{
                 startAdornment: (
                  <InputAdornment position="start">
                    <CalendarTodayOutlinedIcon sx={{ color: mediumText }} />
                  </InputAdornment>
                ),
                sx: { 
                  // Rounded left corners only - challenging with default MUI TextField
                  // Approximating with full border radius for now
                  borderRadius: '25px', 
                  backgroundColor: '#3b5066', 
                  color: lightText,
                   '& fieldset': { border: 'none' }, // Remove default border
                },
              }}
              value={formik.values.dob}
              onChange={formik.handleChange}
              error={formik.touched.dob && Boolean(formik.errors.dob)}
              helperText={formik.touched.dob && formik.errors.dob}
            />
            {/* Password Field */}
            <TextField
              margin="normal"
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="new-password"
              value={formik.values.password}
              onChange={formik.handleChange}
              error={formik.touched.password && Boolean(formik.errors.password)}
              helperText={formik.touched.password && formik.errors.password}
               InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <LockOutlinedIcon sx={{ color: mediumText }} />
                  </InputAdornment>
                ),
                 sx: { 
                  // Rounded left corners only - challenging with default MUI TextField
                  // Approximating with full border radius for now
                  borderRadius: '25px', 
                  backgroundColor: '#3b5066', 
                  color: lightText,
                   '& fieldset': { border: 'none' }, // Remove default border
                },
              }}
               InputLabelProps={{
                sx: { color: mediumText }, // Label color
              }}
            />

            {/* Sign Up Button */}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                mt: 3,
                mb: 2,
                backgroundColor: accentColor, // Use accent color for button
                 '&:hover': {
                  backgroundColor: accentColor, // Keep the same color on hover
                   opacity: 0.9 // Slight opacity change
                },
                color: 'white',
                fontWeight: 'bold',
                fontSize: '1.2rem',
                borderRadius: '25px', // Rounded corners for the button
              }}
              disabled={formik.isSubmitting}
            >
              SIGN UP
            </Button>

            {/* Link to Sign In */}
            <Button
              fullWidth
              variant="text"
              sx={{ color: accentColor, fontWeight: 'bold' }}
              onClick={() => navigate('/login')}
            >
              ALREADY HAVE AN ACCOUNT? SIGN IN
            </Button>
          </Box>
        </Box>
      </Container>
     </Box>
  );
};
