import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  Box,
  Button,
  TextField,
  Typography,
  Container,
  Alert,
  InputAdornment,
  Checkbox,
  FormControlLabel,
  Link,
} from '@mui/material';
import AccountCircleOutlinedIcon from '@mui/icons-material/AccountCircleOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import { useAuth } from '../context/AuthContext';
import { useNavigate, useLocation } from 'react-router-dom';

const validationSchema = Yup.object({
  email: Yup.string()
    .email('Enter a valid email')
    .required('Email is required'),
  password: Yup.string()
    .min(6, 'Password should be of minimum 6 characters length')
    .required('Password is required'),
});

export const LoginForm = () => {
  const { login, error } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const formik = useFormik({
    initialValues: {
      email: '',
      password: '',
      rememberMe: false,
    },
    validationSchema,
    onSubmit: async (values) => {
      await login(values);
      const from = location.state?.from?.pathname || '/dashboard';
      navigate(from, { replace: true });
    },
  });

  // Define colors based on the image
  const primaryDark = '#2c3e50'; // Dark background of the form container
  const accentColor = '#00bcd4'; // Teal/Cyan accent color
  const lightText = '#ecf0f1'; // Very light grey/white for main text/icons
  const mediumText = '#bdc3c7'; // Medium grey for labels and secondary text
  const formBackground = '#3b5066'; // Background color for input fields
  const pageBackgroundDark = '#34495e'; // Dark part of the page background

  return (
    <Box
      sx={{
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        // Updated background to better match the split design
        background: `linear-gradient(to bottom, ${accentColor} 0%, ${accentColor} 50%, ${pageBackgroundDark} 50%, ${pageBackgroundDark} 100%)`,
        // Consider adjusting background size/position if needed for different screen sizes
      }}
    >
      <Container component="main" maxWidth="xs">
        <Box
          sx={{
            paddingTop: '60px', // Add padding to accommodate the absolute positioned header
            paddingX: 4,
            paddingBottom: 4,
            borderRadius: 2,
            backgroundColor: primaryDark,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            boxShadow: '0px 0px 20px rgba(0,0,0,0.5)',
            overflow: 'hidden',
            position: 'relative',
          }}
        >
          {/* "SIGN IN" Header Button - Adjusted positioning and shape */}
          <Button
            variant="contained"
            fullWidth
            sx={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: 50,
              borderBottomLeftRadius: 0,
              borderBottomRightRadius: 0,
              borderTopLeftRadius: 8,
              borderTopRightRadius: 8,
              backgroundColor: accentColor,
              '&:hover': {
                backgroundColor: accentColor,
                opacity: 0.9,
              },
              color: 'white',
              fontWeight: 'bold',
              fontSize: '1.2rem',
              padding: '12px 16px',
              zIndex: 1,
            }}
            onClick={() => navigate('/login')}
          >
            SIGN IN
          </Button>

          {/* Content below the header */}
          <Box sx={{ mt: 8, width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>

            {/* User Icon */}
            <Box
              sx={{
                width: 80,
                height: 80,
                borderRadius: '50%',
                backgroundColor: '#556c7b', // A lighter shade for the icon background
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                mb: 3,
              }}
            >
              <AccountCircleOutlinedIcon sx={{ fontSize: 50, color: lightText }} />
            </Box>

            {error && (
              <Alert severity="error" sx={{ mt: 2, width: '100%', mb: 2 }}>
                {error}
              </Alert>
            )}

            <Box component="form" onSubmit={formik.handleSubmit} sx={{ width: '100%' }}>
              {/* Email Field */}
              <TextField
                margin="normal"
                fullWidth
                id="email"
                name="email"
                label="username"
                autoComplete="email"
                autoFocus
                value={formik.values.email}
                onChange={formik.handleChange}
                error={formik.touched.email && Boolean(formik.errors.email)}
                helperText={formik.touched.email && formik.errors.email}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start" sx={{ color: mediumText }}>
                      <AccountCircleOutlinedIcon />
                    </InputAdornment>
                  ),
                  sx: {
                    borderRadius: '25px',
                     // Refined attempt for right-side flattening/sharpening
                    borderTopRightRadius: '4px',
                    borderBottomRightRadius: '4px',
                    borderTopLeftRadius: '4px',
                    borderBottomLeftRadius: '4px',
                    backgroundColor: formBackground,
                    color: lightText, // Ensure input text color is visible
                    '& fieldset': { border: 'none' },
                    paddingLeft: '14px', // Adjust padding for icon
                  },
                }}
                InputLabelProps={{
                  sx: { 
                    color: mediumText, // Ensure label color is visible
                    fontSize: '1rem', // Increase label font size
                    fontWeight: 'bold', // Make label bold for clarity
                    position: 'relative', // Use relative positioning
                    transform: 'none', // Disable default transform
                    marginBottom: '4px', // Add some space below the label
                    top: 'auto', // Reset top position
                    left: 'auto' // Reset left position
                  }, 
                  shrink: true // Keep label visible
                }}
              />
              {/* Password Field */}
              <TextField
                margin="normal"
                fullWidth
                name="password"
                label="password"
                type="password"
                id="password"
                autoComplete="current-password"
                value={formik.values.password}
                onChange={formik.handleChange}
                error={formik.touched.password && Boolean(formik.errors.password)}
                helperText={formik.touched.password && formik.errors.password}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start" sx={{ color: mediumText }}>
                      <LockOutlinedIcon />
                    </InputAdornment>
                  ),
                   sx: {
                    borderRadius: '25px',
                     // Refined attempt for right-side flattening/sharpening
                    borderTopRightRadius: '4px',
                    borderBottomRightRadius: '4px',
                    borderTopLeftRadius: '4px',
                    borderBottomLeftRadius: '4px',
                    backgroundColor: formBackground,
                    color: lightText, // Ensure input text color is visible
                     '& fieldset': { border: 'none' },
                    paddingLeft: '14px', // Adjust padding for icon
                  },
                }}
                 InputLabelProps={{
                  sx: { 
                    color: mediumText, // Ensure label color is visible
                    fontSize: '1rem', // Increase label font size
                    fontWeight: 'bold', // Make label bold for clarity
                    position: 'relative', // Use relative positioning
                    transform: 'none', // Disable default transform
                    marginBottom: '4px', // Add some space below the label
                     top: 'auto', // Reset top position
                    left: 'auto' // Reset left position
                   },
                  shrink: true // Keep label visible
                }}
              />

              {/* Remember Me and Forgot Password */}
              <Box sx={{ display: 'flex', justifyContent: 'space-between', width: '100%', mt: 1, mb: 2, color: mediumText, fontSize: '0.8rem' }}>
                <FormControlLabel
                  control={
                    <Checkbox
                      value="remember"
                      color="primary"
                      checked={formik.values.rememberMe}
                      onChange={formik.handleChange}
                      name="rememberMe"
                      sx={{ color: accentColor, '&.Mui-checked': { color: accentColor }, '& .MuiSvgIcon-root': { fontSize: '1rem' } }}
                    />
                  }
                  label={<Typography variant="body2" sx={{ color: mediumText, fontSize: '0.8rem' }}>Remember me</Typography>}
                />
                <Link href="#" variant="body2" sx={{ color: accentColor, alignSelf: 'center', fontSize: '0.8rem' }}>
                  Forgot your password?
                </Link>
              </Box>

              {/* Login Button */}
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{
                  mt: 1,
                  mb: 2,
                  backgroundColor: accentColor,
                   '&:hover': {
                    backgroundColor: accentColor,
                     opacity: 0.9,
                  },
                  color: 'white',
                  fontWeight: 'bold',
                  fontSize: '1.2rem',
                  borderRadius: '25px',
                }}
                disabled={formik.isSubmitting}
              >
                LOGIN
              </Button>

              {/* Link to Register */}
              <Button
                fullWidth
                variant="text"
                sx={{ color: accentColor, fontWeight: 'bold', fontSize: '0.9rem' }}
                onClick={() => navigate('/register')}
              >
                DON'T HAVE AN ACCOUNT? SIGN UP
              </Button>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
};
